const express = require('express');
const cors = require('cors');
require('dotenv').config();

// âœ… Import Sequelize models (index.js is auto-generated by sequelize-cli)
const db = require('../models');

// âœ… Import routes
const authRoutes = require('./routes/authRoutes');
// (you can later add more like roleRoutes, permissionRoutes, etc.)

const app = express();

// âœ… Middlewares
app.use(cors());
app.use(express.json());

// âœ… Health-check route
app.get('/', (req, res) => {
  res.send('âœ… RBAC Backend is running...');
});

// âœ… Mount routes
app.use('/auth', authRoutes);

// âœ… Port from .env or default
const PORT = process.env.PORT || 5000;

// âœ… Connect to DB and sync models, then start server
db.sequelize.authenticate()
  .then(() => {
    console.log('âœ… Database connected');
    return db.sequelize.sync(); // sync models & associations with database
  })
  .then(() => {
    app.listen(PORT, () => {
      console.log(`ğŸš€ Server running on port ${PORT}`);
    });
  })
  .catch((err) => {
    console.error('âŒ DB connection error:', err);
  });
